var displayData = () => {
    // let errorCount = global.get('deviceProfile_2_errorCount');
    // if(errorCount == 1){
    // node.warn('Data read successfully After Retry DeviceProfile-2')
    // global.set('deviceProfile_2_errorCount', null);
    // }

    let data = [];
    let getStoreData = global.get('deviceProfile_2_StoreData');

    getStoreData.forEach(item => {
        item.forEach(value => {
            data.push(value);
        })
    })

    let finalData = data.toString();
    let deviceType = profile.deviceProfile.deviceType;
    let deviceNo = profile.deviceProfile.deviceNo;
    let dateTime = msg.timeStamp;

    msg.payloadData = {
     request: {
        "PlantID": 172,
        "LoggerNo": 1,
        "DeviceType": deviceType,
        "DeviceNo": deviceNo,
        "Data": finalData,
        "TimeStamp": dateTime,
        "Error": 0,
     }
    }
    global.set('deviceProfile_2_StoreData', []);
}   

var storeData = () => {
    // msg.payload = [1,2,3,4,5];
    let getStoreData = global.get('deviceProfile_2_StoreData');
    getStoreData.push(msg.payload);
    global.set('deviceProfile_2_StoreData', getStoreData);
}

if(dataPacket_Count != number_Of_DataPacket){
  // Storing Data  
  storeData();
  let index = global.get('deviceProfile_2_ReadIndex');
  index += 1;  
  global.set('deviceProfile_2_ReadIndex', index);  
  global.set('stop_deviceProfile_2', false);
  global.set('stop_to_read_deviceProfile_2_dataPacket', true);

}else {
    // Storing Data  
    storeData();
    global.set('stop_to_read_deviceProfile_2_dataPacket', false);
    global.set('deviceProfile_2_ReadIndex', 0);
    global.set("deviceProfile_2_dataPacket_Count", null);
    global.set('send_Data_deviceProfile_2', true);
    // display Data
    displayData();

    // Number of Device Loop
    var deviceCount =  global.get("deviceProfile_2_Count");
    deviceCount += 1;
    global.set('deviceProfile_2_Count', deviceCount);

    if(deviceCount != number_of_device ){
        let index = global.get('deviceProfile_2_Index');
        index += 1;
        global.set('deviceProfile_2_Index', index);
        global.set('stop_deviceProfile_2', true);
        global.set('stop_to_read_deviceProfile_2_dataPacket', false);
    }else {
        global.set('stop_to_read_deviceProfile_2_dataPacket', false);
        global.set('stop_deviceProfile_2', false);
        global.set('deviceProfile_2_Count', null);
    }
}

return msg;

