let deviceProfile_ErrorConfig = (number) => {
        // For RetryToRead function Configration
        let deviceProfile_Index = `deviceProfile_${number}_Index`;
        let deviceProfile_dataPacket_Count = `deviceProfile_${number}_dataPacket_Count`;
        let deviceProfile_errorCount = `deviceProfile_${number}_errorCount`;
        let deviceProfile_StoreData = `deviceProfile_${number}_StoreData`;
        let deviceProfile_ReadIndex = `deviceProfile_${number}_ReadIndex`;

        // For DisplayError function Configration
        let deviceProfile = `deviceProfile_${number}`;

        // For MoveOnNextDevice function Configration
        let stop_to_read_deviceProfile_dataPacket = `stop_to_read_deviceProfile_${number}_dataPacket`;
        let deviceProfile_Count = `deviceProfile_${number}_Count`;
        let stop_deviceProfile = `stop_deviceProfile_${number}`;

        // Calling RetryToRead
        retryToRead(
            deviceProfile_Index,
            deviceProfile_dataPacket_Count,
            deviceProfile_errorCount,
            deviceProfile_StoreData,
            deviceProfile_ReadIndex,
            deviceProfile,
            stop_to_read_deviceProfile_dataPacket,
            deviceProfile_Count,
            stop_deviceProfile
        );
}


