var timeStamp = () => {
     let dateObj = new Date(),
        month = '' + (dateObj.getMonth() + 1),
        day = '' + dateObj.getDate(),
        year = dateObj.getFullYear();

    if (month.length < 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;

    let time = dateObj.toLocaleTimeString('en-IN', {timeZone: 'Asia/Kolkata', hour12: false });
    let dateTime = `${year}-${month}-${day} ${time}`;
    return dateTime;
}

var displayData = () => {
    // let errorCount = global.get('deviceProfile_15_errorCount');
    // if(errorCount == 1){
    // node.warn('Data read successfully After Retry DeviceProfile-15')
    // global.set('deviceProfile_15_errorCount', null);
    // }

    let data = [];
    let getStoreData = global.get('deviceProfile_15_StoreData');

    getStoreData.forEach(item => {
        item.forEach(value => {
            data.push(value);
        })
    })

    let finalData = data.toString();
    let deviceType = profile.deviceProfile.deviceType;
    let deviceNo = profile.deviceProfile.deviceNo;
    let dateTime = timeStamp();

    msg.payloadData = {
     request: {
        "PlantID": 172,
        "LoggerNo": 1,
        "DeviceType": deviceType,
        "DeviceNo": deviceNo,
        "Data": finalData,
        "TimeStamp": dateTime,
        "Error": 0,
     }
    }
    global.set('deviceProfile_15_StoreData', []);
}   

