  global.set('stop_deviceProfile_15', false);
  global.set('stop_to_read_deviceProfile_15_dataPacket', true);

}else {
    // Storing Data  
    storeData();
    global.set('stop_to_read_deviceProfile_15_dataPacket', false);
    global.set('deviceProfile_15_ReadIndex', 0);
    global.set("deviceProfile_15_dataPacket_Count", null);
    global.set('send_Data_deviceProfile_15', true);
