        global.set('deviceProfile_22_Index', index);
        global.set('stop_deviceProfile_22', true);
        global.set('stop_to_read_deviceProfile_22_dataPacket', false);
    }else {
        global.set('stop_to_read_deviceProfile_22_dataPacket', false);
        global.set('stop_deviceProfile_22', false);
        global.set('deviceProfile_22_Count', null);
    }
}

