var device_Profile = global.get('deviceProfile_1');
var index = global.get('deviceProfile_1_Index');
var profile = device_Profile[index];
// node.warn(profile)
var regAdd = profile.read.regAdd;
var readIndex = global.get('deviceProfile_1_ReadIndex');
var readValue = regAdd[readIndex];
// node.warn(readValue)
var slaveID = profile.read.slaveID;
// var slaveID = 2
var fc = readValue.fc;
// var fc = 3
var address = readValue.address;
var quantity = readValue.quantity;

global.set('send_Data_deviceProfile_1', false);
readData(fc, slaveID, address, quantity);

return msg;

var device_profile = global.get('deviceProfile_1');
var number_of_device = device_profile.length;

var index = global.get('deviceProfile_1_Index');
var profile = device_profile[index];
var regAdd =  profile.read.regAdd;
var number_Of_DataPacket = regAdd.length;

global.set('stop_to_read_deviceProfile_1_dataPacket', false);

var dataPacket_Count =  global.get("deviceProfile_1_dataPacket_Count") || 0;
    dataPacket_Count += 1;
    global.set('deviceProfile_1_dataPacket_Count', dataPacket_Count);

var displayData = () => {
    let errorCount = global.get('deviceProfile_1_errorCount');
    if(errorCount == 1){
    node.warn('Data read successfully After Retry DeviceProfile-1')
    global.set('deviceProfile_1_errorCount', null);
    }

    let data = [];
    let getStoreData = global.get('deviceProfile_1_StoreData');

    getStoreData.forEach(item => {
        item.forEach(value => {
            data.push(value);
        })
    })

    let finalData = data.toString();
    let deviceType = profile.deviceProfile.deviceType;
    let deviceNo = profile.deviceProfile.deviceNo;
    let dateTime = msg.timeStamp;

    msg.payloadData = {
     request: {
        "PlantID": 172,
        "LoggerNo": 1,
        "DeviceType": deviceType,
        "DeviceNo": deviceNo,
        "Data": finalData,
        "TimeStamp": dateTime,
        "Error": 0,
        "DataLength": data.length
     }
    }
    global.set('deviceProfile_1_StoreData', []);
}   

var storeData = () => {
    // msg.payload = [1,2,3,4,5];
    let getStoreData = global.get('deviceProfile_1_StoreData');
    getStoreData.push(msg.payload);
    global.set('deviceProfile_1_StoreData', getStoreData);
}

if(dataPacket_Count != number_Of_DataPacket){
  // Storing Data  
  storeData();
  let index = global.get('deviceProfile_1_ReadIndex');
  index += 1;  
  global.set('deviceProfile_1_ReadIndex', index);  
  global.set('stop_deviceProfile_1', false);
  global.set('stop_to_read_deviceProfile_1_dataPacket', true);

}else {
    // Storing Data  
    storeData();
    global.set('stop_to_read_deviceProfile_1_dataPacket', false);
    global.set('deviceProfile_1_ReadIndex', 0);
    global.set("deviceProfile_1_dataPacket_Count", null);
    global.set('send_Data_deviceProfile_1', true);
    // display Data
    displayData();

    // Number of Device Loop
    var deviceCount =  global.get("deviceProfile_1_Count");
    deviceCount += 1;
    global.set('deviceProfile_1_Count', deviceCount);

    if(deviceCount != number_of_device ){
        let index = global.get('deviceProfile_1_Index');
        index += 1;
        global.set('deviceProfile_1_Index', index);
        global.set('stop_deviceProfile_1', true);
        global.set('stop_to_read_deviceProfile_1_dataPacket', false);
    }else {
        global.set('stop_to_read_deviceProfile_1_dataPacket', false);
        global.set('stop_deviceProfile_1', false);
        global.set('deviceProfile_1_Count', null);
    }
}

return msg;

