var displayError = (deviceProfile, deviceProfile_Index) => {
    node.warn('displayError')
    msg.display_Error = 'displayError';
    let device_Profile = global.get(deviceProfile);
    let index = global.get(deviceProfile_Index);
    let profile = device_Profile[index];

    let data = null;
    let deviceType = profile.deviceProfile.deviceType;
    let deviceNo = profile.deviceProfile.deviceNo;
    let dateTime = msg.timeStamp;

    msg.payloadData = {
     request: {
        "PlantID": 172,
        "LoggerNo": 1,
        "DeviceType": deviceType,
        "DeviceNo": deviceNo,
        "Data": data,
        "TimeStamp": dateTime,
        "Error": 1,
     }
    }
}  

var moveOnNextDevice = (
            deviceProfile_StoreData,
            deviceProfile,
            stop_to_read_deviceProfile_dataPacket,
            deviceProfile_Count,
            deviceProfile_Index,
            deviceProfile_ReadIndex,
            stop_deviceProfile
            ) => {
    node.warn('moveOnNextDevice')
    msg.error_Status = 'moveOnNextDevice';
    global.set(deviceProfile_StoreData, []);
    let device_profile = global.get(deviceProfile);
    let number_of_device = device_profile.length;

    global.set(stop_to_read_deviceProfile_dataPacket, false);
    let deviceCount =  global.get(deviceProfile_Count);
    deviceCount += 1;
    global.set(deviceProfile_Count, deviceCount);

    let index = global.get(deviceProfile_Index);
    index += 1;
    global.set(deviceProfile_Index, index);
    global.set(deviceProfile_ReadIndex, 0);

    if(deviceCount == number_of_device){
        node.warn('number_of_device If condition');
        msg.error_Status = '';
        global.set(deviceProfile_Count, null);
        global.set(stop_deviceProfile, false);
        global.set(stop_to_read_deviceProfile_dataPacket, false);
    }
}

let retryToRead = (
        deviceProfile_Index,
        deviceProfile_dataPacket_Count,
        deviceProfile_errorCount,
        deviceProfile_StoreData,
        deviceProfile_ReadIndex,
        deviceProfile,
        stop_to_read_deviceProfile_dataPacket,
        deviceProfile_Count,
        stop_deviceProfile
        )  => {
        let index = global.get(deviceProfile_Index);

        global.set(deviceProfile_dataPacket_Count, null);

        // displayError(deviceProfile, deviceProfile_Index);

            // Calling MoveOnNextDevice Function
            // moveOnNextDevice(
            //     deviceProfile_StoreData,
            //     deviceProfile,
            //     stop_to_read_deviceProfile_dataPacket,
            //     deviceProfile_Count,
            //     deviceProfile_Index,
            //     deviceProfile_ReadIndex,
            //     stop_deviceProfile
            // );

        let errorCount = global.get(deviceProfile_errorCount) || 0;
        errorCount += 1;
        node.warn(errorCount + ' errorCount')
        global.set(deviceProfile_errorCount, errorCount);

        if(errorCount == 1){
            // node.warn('If Block')
            global.set(deviceProfile_StoreData, []);
            msg.error_Status = 'retry';
            global.set(stop_to_read_deviceProfile_dataPacket, true);
            global.set(deviceProfile_Index, index);
            global.set(deviceProfile_ReadIndex, 0);   
        }else {
            // node.warn('else')
            displayError(deviceProfile, deviceProfile_Index);
            global.set(deviceProfile_errorCount, null);

            // Calling MoveOnNextDevice Function
            moveOnNextDevice(
                deviceProfile_StoreData,
                deviceProfile,
                stop_to_read_deviceProfile_dataPacket,
                deviceProfile_Count,
                deviceProfile_Index,
                deviceProfile_ReadIndex,
                stop_deviceProfile
            );
        }
    }


let deviceProfile_ErrorConfig = (number) => {
        // For RetryToRead function Configration
        let deviceProfile_Index = `deviceProfile_${number}_Index`;
        let deviceProfile_dataPacket_Count = `deviceProfile_${number}_dataPacket_Count`;
        let deviceProfile_errorCount = `deviceProfile_${number}_errorCount`;
        let deviceProfile_StoreData = `deviceProfile_${number}_StoreData`;
        let deviceProfile_ReadIndex = `deviceProfile_${number}_ReadIndex`;

        // For DisplayError function Configration
        let deviceProfile = `deviceProfile_${number}`;

        // For MoveOnNextDevice function Configration
        let stop_to_read_deviceProfile_dataPacket = `stop_to_read_deviceProfile_${number}_dataPacket`;
        let deviceProfile_Count = `deviceProfile_${number}_Count`;
        let stop_deviceProfile = `stop_deviceProfile_${number}`;

        // Calling RetryToRead
        retryToRead(
            deviceProfile_Index,
            deviceProfile_dataPacket_Count,
            deviceProfile_errorCount,
            deviceProfile_StoreData,
            deviceProfile_ReadIndex,
            deviceProfile,
            stop_to_read_deviceProfile_dataPacket,
            deviceProfile_Count,
            stop_deviceProfile
        );
}


