    global.set('deviceProfile_3_StoreData', []);
}   

var storeData = () => {
    // msg.payload = [1,2,3,4,5];
    let getStoreData = global.get('deviceProfile_3_StoreData');
    getStoreData.push(msg.payload);
    global.set('deviceProfile_3_StoreData', getStoreData);
}

if(dataPacket_Count != number_Of_DataPacket){
  // Storing Data  
  storeData();
  let index = global.get('deviceProfile_3_ReadIndex');
  index += 1;  
  global.set('deviceProfile_3_ReadIndex', index);  
  global.set('stop_deviceProfile_3', false);
  global.set('stop_to_read_deviceProfile_3_dataPacket', true);

}else {
    // Storing Data  
    storeData();
    global.set('stop_to_read_deviceProfile_3_dataPacket', false);
    global.set('deviceProfile_3_ReadIndex', 0);
    global.set("deviceProfile_3_dataPacket_Count", null);
    global.set('send_Data_deviceProfile_3', true);
    // display Data
    displayData();

    // Number of Device Loop
    var deviceCount =  global.get("deviceProfile_3_Count");
    deviceCount += 1;
    global.set('deviceProfile_3_Count', deviceCount);

    if(deviceCount != number_of_device ){
        let index = global.get('deviceProfile_3_Index');
        index += 1;
        global.set('deviceProfile_3_Index', index);
        global.set('stop_deviceProfile_3', true);
        global.set('stop_to_read_deviceProfile_3_dataPacket', false);
    }else {
        global.set('stop_to_read_deviceProfile_3_dataPacket', false);
        global.set('stop_deviceProfile_3', false);
        global.set('deviceProfile_3_Count', null);
    }
}

return msg;
