var displayData = () => {
    let data = [];
    let getStoreData = global.get('PLC_deviceProfile_1_StoreData');

    getStoreData.forEach(item => {
        item.forEach(value => {
            data.push(value);
        })
    })

    let finalData = data.toString();
    let deviceType = profile.deviceProfile.deviceType;
    let deviceNo = profile.deviceProfile.deviceNo;
    let dateTime = msg.timeStamp;

    msg.payloadData = {
     request: {
        "PlantID": 201,
        "LoggerNo": 1,
        "DeviceType": deviceType,
        "DeviceNo": deviceNo,
        "Data": finalData,
        "TimeStamp": dateTime,
        "Error": 0,
        "dataLength": data.length
     }
    }
    global.set('PLC_deviceProfile_1_StoreData', []);
}      

var storeData = () => {
    // msg.payload = [1,2,3,4,5];
    let getStoreData = global.get('PLC_deviceProfile_1_StoreData');
    getStoreData.push(msg.payload);
    global.set('PLC_deviceProfile_1_StoreData', getStoreData);
}

// Inside Loop                                                              
if(dataPacket_Count != number_Of_DataPacket){
    // Store Data
    // node.warn(dataPacket_Count)
    storeData()
    global.set('stop_to_read_PLC_deviceProfile_1_dataPacket', true);
    global.set('stop_PLC_deviceProfile_1', false);
    let readIndex = global.get('PLC_deviceProfile_1_ReadIndex');
    readIndex += 1;
    global.set('PLC_deviceProfile_1_ReadIndex', readIndex);

