var moveOnNextDevice = () => {
    let deviceCount =  global.get("PLC_deviceProfile_1_Count");
    deviceCount += 1;
    global.set('PLC_deviceProfile_1_Count', deviceCount);

    let index = global.get('PLC_deviceProfile_1_Index');
    index += 1;
    global.set('PLC_deviceProfile_1_Index', index);
    global.set('stop_PLC_deviceProfile_1', true);
    // node.warn('Number of device on error-1')
    if(deviceCount == number_Of_Device ){
        // node.warn('MoveOnNextDevice stop_PLC_deviceProfile_1')
        global.set('stop_PLC_deviceProfile_1', false);
        global.set('PLC_deviceProfile_1_Index', 0);
        // global.set('PLC_deviceProfile_1_Count', null);
        // node.warn('Number of device on error-2')
    }
}

if(global.get('PLC_deviceProfile_1_retryStatus') == 'displayErrorMsg'){
